    setUrl: function(urlLink, url, urlIndex) {
      var urlLinkTmp;
      if ( urlLink.indexOf("://") === 4 || urlLink.indexOf("://") === 5 ) {
        urlLinkTmp = urlLink;
      } else {
        switch ( urlIndex ) {
          case 0:
          case 1:
            urlLinkTmp = url;
            urlLinkTmp += urlLink;
            break;
          case 2:
            urlLinkTmp = "http://";
            if ( urlLink.indexOf("www.") > 0 ) {
              urlLinkTmp += urlLink;
            } else {
              if ( urlLink.indexOf(".") === 0 ) {
                urlLinkTmp += url;
                urlLinkTmp += urlLink;
              } else {
                urlLinkTmp += url;
                urlLinkTmp += ".";
                urlLinkTmp += urlLink;
              }
            }
            break;
        };
      }
      return urlLinkTmp;
    },
